import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import ChatWindow from './ChatWindow';
import NotificationDropdown from './NotificationDropdown';
import { notificationApi } from '../../api/notification';
import { messageApi } from '../../api/message';
import './Header.scss';

const Header: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [nickname, setNickname] = useState('');
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [unreadMessageCount, setUnreadMessageCount] = useState(0);

  useEffect(() => {
    // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ (Í≤ΩÎ°ú Î≥ÄÍ≤Ω ÏãúÎßàÎã§ Ï≤¥ÌÅ¨)
    const userId = localStorage.getItem('userId');
    const userNickname = localStorage.getItem('nickname');

    console.log('Header - userId:', userId);
    console.log('Header - nickname:', userNickname);

    if (userId) {
      setIsLoggedIn(true);
      setNickname(userNickname || '');
      console.log('Header - Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÎ°ú ÏÑ§Ï†ï');
    } else {
      setIsLoggedIn(false);
      setNickname('');
      console.log('Header - Î°úÍ∑∏ÏïÑÏõÉ ÏÉÅÌÉúÎ°ú ÏÑ§Ï†ï');
    }
  }, [location]);

  // ÏùΩÏßÄ ÏïäÏùÄ ÏïåÎ¶º Ïàò Ï°∞Ìöå
  useEffect(() => {
    const fetchUnreadCount = async () => {
      if (isLoggedIn) {
        try {
          const count = await notificationApi.getUnreadCount();
          setUnreadCount(count);
        } catch (err) {
          console.error('ÏùΩÏßÄ ÏïäÏùÄ ÏïåÎ¶º Ïàò Ï°∞Ìöå Ïã§Ìå®:', err);
        }
      }
    };

    fetchUnreadCount();

    // 30Ï¥àÎßàÎã§ Í∞±Ïã†
    const interval = setInterval(fetchUnreadCount, 30000);

    return () => clearInterval(interval);
  }, [isLoggedIn]);

  // ÏùΩÏßÄ ÏïäÏùÄ Î©îÏãúÏßÄ Ïàò Ï°∞Ìöå
  useEffect(() => {
    const fetchUnreadMessageCount = async () => {
      if (isLoggedIn) {
        try {
          const count = await messageApi.getUnreadCount();
          setUnreadMessageCount(count);
        } catch (err) {
          console.error('ÏùΩÏßÄ ÏïäÏùÄ Î©îÏãúÏßÄ Ïàò Ï°∞Ìöå Ïã§Ìå®:', err);
        }
      }
    };

    fetchUnreadMessageCount();

    // 30Ï¥àÎßàÎã§ Í∞±Ïã†
    const interval = setInterval(fetchUnreadMessageCount, 30000);

    return () => clearInterval(interval);
  }, [isLoggedIn]);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/search?q=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery('');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('username');
    localStorage.removeItem('nickname');
    setIsLoggedIn(false);
    setNickname('');
    navigate('/');
  };

  const boards = [
    { id: 1, name: 'Ï§ëÍ≥†Ïû•ÌÑ∞' },
    { id: 2, name: 'ÏûêÏú†Í≤åÏãúÌåê' },
    { id: 3, name: 'Ïù¥Î≤§Ìä∏Í≤åÏãúÌåê' },
    { id: 4, name: 'ÏßàÎ¨∏Í≤åÏãúÌåê' },
    { id: 5, name: 'Ïù∏Í∏∞Í∏Ä' },
  ];

  const handleTimetableClick = () => {
    navigate('/timetable');
  };

  const handleLectureReviewClick = () => {
    navigate('/lecture-reviews');
  };

  return (
    <header className="main-header">
      <div className="header-container">
        <div className="logo-section">
          <div className="logo-image" onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
            {/* TODO: Î°úÍ≥† Ïù¥ÎØ∏ÏßÄ Ï∂îÍ∞Ä */}
            <span className="logo-text">„Éù„É™</span>
          </div>
        </div>

        <div className="title-section">
          <h1 className="site-title" onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
            GLOBALIN
          </h1>
          <p className="site-subtitle">ÏùºÎ≥∏ Ïú†ÌïôÏÉù Ïª§ÎÆ§ÎãàÌã∞</p>
        </div>

        <div className="search-section">
          <form onSubmit={handleSearch} className="search-form">
            <input
              type="text"
              placeholder="ÏÜå Í≤åÏãúÎ¨º Í≤ÄÏÉâ..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button type="submit" className="search-button">
              üîç
            </button>
          </form>
        </div>

        <div className="chat-section">
          <button
            className="chat-button"
            onClick={() => setIsChatOpen(!isChatOpen)}
            title="Ï†ÑÏ≤¥ Ï±ÑÌåÖÎ∞©"
          >
            üí¨
          </button>
        </div>

        {isLoggedIn && (
          <>
            <div className="timetable-section">
              <button
                className="timetable-button"
                onClick={handleTimetableClick}
                title="ÏãúÍ∞ÑÌëú"
              >
                üìÖ
              </button>
            </div>

            <div className="message-section">
              <button
                className="message-button"
                onClick={() => navigate('/messages')}
                title="Î©îÏãúÏßÄ"
              >
                ‚úâÔ∏è
                {unreadMessageCount > 0 && (
                  <span className="message-badge">{unreadMessageCount > 99 ? '99+' : unreadMessageCount}</span>
                )}
              </button>
            </div>

            <div className="notification-section">
              <button
                className="notification-button"
                onClick={() => setIsNotificationOpen(!isNotificationOpen)}
                title="ÏïåÎ¶º"
              >
                üîî
                {unreadCount > 0 && (
                  <span className="notification-badge">{unreadCount > 99 ? '99+' : unreadCount}</span>
                )}
              </button>
              <NotificationDropdown
                isOpen={isNotificationOpen}
                onClose={() => setIsNotificationOpen(false)}
              />
            </div>
          </>
        )}

        <div className="auth-section">
          {isLoggedIn ? (
            <div className="user-info">
              <span className="user-nickname">{nickname}Îãò</span>
              <button className="logout-button" onClick={handleLogout}>
                Î°úÍ∑∏ÏïÑÏõÉ
              </button>
            </div>
          ) : (
            <div className="auth-buttons">
              <button className="login-button" onClick={() => navigate('/login')}>
                Î°úÍ∑∏Ïù∏
              </button>
              <button className="register-button" onClick={() => navigate('/register')}>
                ÌöåÏõêÍ∞ÄÏûÖ
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Í≤åÏãúÌåê ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
      <nav className="board-nav">
        <div className="board-nav-container">
          {boards.map((board) => (
            <button
              key={board.id}
              className="board-nav-item"
              onClick={() => navigate(`/board/${board.id}`)}
            >
              {board.name}
            </button>
          ))}
        </div>
      </nav>

      {/* Ï±ÑÌåÖÏ∞Ω */}
      <ChatWindow isOpen={isChatOpen} onClose={() => setIsChatOpen(false)} />
    </header>
  );
};

export default Header;
