import React from 'react';
import { Post } from '../../../types';
import { useNavigate } from 'react-router-dom';
import './MainContent.scss';

interface BoardWithPosts {
  id: number;
  name: string;
  description: string;
  category: string;
  icon: string;
  postCount: number;
  recentPosts: Post[];
}

interface MainContentProps {
  humanitiesPosts: Post[];
  freePosts: Post[];
  allBoards?: BoardWithPosts[];
}

const MainContent: React.FC<MainContentProps> = ({ humanitiesPosts, freePosts }) => {
  const navigate = useNavigate();

  const getTimeAgo = (timestamp: string | number): string => {
    const now = new Date();
    const past = new Date(timestamp);
    const diffInSeconds = Math.floor((now.getTime() - past.getTime()) / 1000);

    if (diffInSeconds < 60) {
      return '방금 전';
    } else if (diffInSeconds < 3600) {
      const minutes = Math.floor(diffInSeconds / 60);
      return `${minutes}분 전`;
    } else if (diffInSeconds < 86400) {
      const hours = Math.floor(diffInSeconds / 3600);
      return `${hours}시간 전`;
    } else if (diffInSeconds < 604800) {
      const days = Math.floor(diffInSeconds / 86400);
      return `${days}일 전`;
    } else {
      return past.toLocaleDateString('ko-KR', { month: 'short', day: 'numeric' });
    }
  };

  const handleAuthorClick = (e: React.MouseEvent, authorId: number) => {
    e.stopPropagation();
    navigate(`/profile/${authorId}`);
  };

  const renderPostList = (posts: Post[], title: string) => (
    <div className="post-section">
      <h2 className="section-title">{title}</h2>
      <div className="post-list">
        <div className="post-list-header">
          <div className="col-title">제목</div>
          <div className="col-author">작성자</div>
          <div className="col-views">조회</div>
          <div className="col-date">작성시간</div>
        </div>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div
              key={post.id}
              className="post-item"
              onClick={() => navigate(`/posts/${post.id}`)}
            >
              <div className="col-title">
                <span className="title-text">{post.title}</span>
                {post.commentCount > 0 && (
                  <span className="comment-count">[{post.commentCount}]</span>
                )}
              </div>
              <div
                className="col-author author-link"
                onClick={(e) => handleAuthorClick(e, post.authorId)}
              >
                {post.author}
              </div>
              <div className="col-views">{post.viewCount}</div>
              <div className="col-date">{getTimeAgo(post.createdAt)}</div>
            </div>
          ))
        ) : (
          <div className="no-posts">게시글이 없습니다.</div>
        )}
      </div>
    </div>
  );

  return (
    <div className="main-content">
      {renderPostList(humanitiesPosts, '중고장터')}
      {renderPostList(freePosts, '자유게시판')}
    </div>
  );
};

export default MainContent;
