import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { authApi, LoginRequest } from '../../../api/auth';
import './LoginPage.scss';

const LoginPage: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<LoginRequest>({
    username: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await authApi.login(formData);

      if (response.success) {
        // 로그인 성공 시 사용자 정보 저장 (localStorage 또는 상태 관리)
        if (response.userId) {
          localStorage.setItem('userId', response.userId.toString());
          localStorage.setItem('username', response.username || '');
          localStorage.setItem('nickname', response.nickname || '');

          alert(response.message);
          // 페이지 전체 리로드로 헤더 상태 업데이트
          window.location.href = '/';
        } else {
          setError('로그인 정보를 받아오지 못했습니다. 다시 시도해주세요.');
        }
      } else {
        setError(response.message);
      }
    } catch (err) {
      setError('서버와의 통신 중 오류가 발생했습니다.');
      console.error('로그인 오류:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1 className="login-title">로그인</h1>
        <p className="login-subtitle">Globalin에 다시 오신 것을 환영합니다</p>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="username">아이디</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="아이디를 입력하세요"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">비밀번호</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="비밀번호를 입력하세요"
              required
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? '처리 중...' : '로그인'}
          </button>
        </form>

        <div className="auth-links">
          <a href="/find-username" onClick={(e) => { e.preventDefault(); navigate('/find-username'); }}>
            아이디 찾기
          </a>
          <span className="divider">|</span>
          <a href="/find-password" onClick={(e) => { e.preventDefault(); navigate('/find-password'); }}>
            비밀번호 찾기
          </a>
          <span className="divider">|</span>
          <a href="/register" onClick={(e) => { e.preventDefault(); navigate('/register'); }}>
            회원가입
          </a>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
