const API_BASE_URL = 'http://localhost:8080/api';

export interface Timetable {
  id?: number;
  userId?: number;
  semester: string;
  year: number;
  name: string;
  isDefault?: boolean;
  createdAt?: string;
  updatedAt?: string;
}

export interface Course {
  id?: number;
  timetableId?: number;
  courseName: string;
  professor?: string;
  location?: string;
  dayOfWeek: number; // 0=월, 1=화, 2=수, 3=목, 4=금
  startTime: string; // "09:00"
  endTime: string; // "10:30"
  color?: string;
  credits?: number;
  memo?: string;
  createdAt?: string;
  updatedAt?: string;
}

export interface ApiResponse {
  success: boolean;
  message: string;
}

export const timetableApi = {
  // 시간표 생성
  createTimetable: async (timetable: Timetable): Promise<Timetable> => {
    const response = await fetch(`${API_BASE_URL}/timetables`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(timetable),
    });

    if (!response.ok) {
      throw new Error('시간표 생성에 실패했습니다.');
    }

    return response.json();
  },

  // 사용자의 모든 시간표 조회
  getTimetables: async (): Promise<Timetable[]> => {
    const response = await fetch(`${API_BASE_URL}/timetables`, {
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('시간표 목록 조회에 실패했습니다.');
    }

    return response.json();
  },

  // 기본 시간표 조회
  getDefaultTimetable: async (): Promise<Timetable> => {
    const response = await fetch(`${API_BASE_URL}/timetables/default`, {
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('기본 시간표 조회에 실패했습니다.');
    }

    return response.json();
  },

  // 시간표 수정
  updateTimetable: async (id: number, timetable: Timetable): Promise<ApiResponse> => {
    const response = await fetch(`${API_BASE_URL}/timetables/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(timetable),
    });

    if (!response.ok) {
      throw new Error('시간표 수정에 실패했습니다.');
    }

    return response.json();
  },

  // 시간표 삭제
  deleteTimetable: async (id: number): Promise<ApiResponse> => {
    const response = await fetch(`${API_BASE_URL}/timetables/${id}`, {
      method: 'DELETE',
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('시간표 삭제에 실패했습니다.');
    }

    return response.json();
  },

  // 과목 추가
  addCourse: async (timetableId: number, course: Course): Promise<Course> => {
    const response = await fetch(`${API_BASE_URL}/timetables/${timetableId}/courses`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(course),
    });

    if (!response.ok) {
      throw new Error('과목 추가에 실패했습니다.');
    }

    return response.json();
  },

  // 시간표의 모든 과목 조회
  getCourses: async (timetableId: number): Promise<Course[]> => {
    const response = await fetch(`${API_BASE_URL}/timetables/${timetableId}/courses`, {
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('과목 목록 조회에 실패했습니다.');
    }

    return response.json();
  },

  // 과목 수정
  updateCourse: async (id: number, course: Course): Promise<ApiResponse> => {
    const response = await fetch(`${API_BASE_URL}/timetables/courses/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(course),
    });

    if (!response.ok) {
      throw new Error('과목 수정에 실패했습니다.');
    }

    return response.json();
  },

  // 과목 삭제
  deleteCourse: async (id: number): Promise<ApiResponse> => {
    const response = await fetch(`${API_BASE_URL}/timetables/courses/${id}`, {
      method: 'DELETE',
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('과목 삭제에 실패했습니다.');
    }

    return response.json();
  },
};
