const API_BASE_URL = 'http://localhost:8080/api';

export interface Report {
  id: number;
  reporterId: number;
  postId?: number;
  userId?: number;
  reason: string;
  status: string;
  createdAt: string;
  resolvedAt?: string;
  adminNote?: string;
  reporterNickname?: string;
  postTitle?: string;
  reportedUserNickname?: string;
}

export interface ReportResponse {
  success: boolean;
  message: string;
}

export const reportApi = {
  // 게시글 신고
  reportPost: async (postId: number, reason: string): Promise<ReportResponse> => {
    const response = await fetch(`${API_BASE_URL}/reports/post/${postId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({ reason }),
    });

    if (!response.ok) {
      throw new Error('게시글 신고에 실패했습니다.');
    }

    return response.json();
  },

  // 사용자 신고
  reportUser: async (userId: number, reason: string): Promise<ReportResponse> => {
    const response = await fetch(`${API_BASE_URL}/reports/user/${userId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({ reason }),
    });

    if (!response.ok) {
      throw new Error('사용자 신고에 실패했습니다.');
    }

    return response.json();
  },

  // 모든 신고 조회 (관리자용)
  getAllReports: async (): Promise<Report[]> => {
    const response = await fetch(`${API_BASE_URL}/reports`, {
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('신고 목록 조회에 실패했습니다.');
    }

    return response.json();
  },

  // 대기 중인 신고 조회 (관리자용)
  getPendingReports: async (): Promise<Report[]> => {
    const response = await fetch(`${API_BASE_URL}/reports/pending`, {
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('대기 중인 신고 조회에 실패했습니다.');
    }

    return response.json();
  },

  // 대기 중인 신고 개수 (관리자용)
  getPendingReportCount: async (): Promise<number> => {
    const response = await fetch(`${API_BASE_URL}/reports/pending/count`, {
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('신고 개수 조회에 실패했습니다.');
    }

    return response.json();
  },

  // 신고 처리 (승인)
  resolveReport: async (id: number, adminNote?: string): Promise<ReportResponse> => {
    const response = await fetch(`${API_BASE_URL}/reports/${id}/resolve`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({ adminNote: adminNote || '' }),
    });

    if (!response.ok) {
      throw new Error('신고 처리에 실패했습니다.');
    }

    return response.json();
  },

  // 신고 거부
  rejectReport: async (id: number, adminNote?: string): Promise<ReportResponse> => {
    const response = await fetch(`${API_BASE_URL}/reports/${id}/reject`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({ adminNote: adminNote || '' }),
    });

    if (!response.ok) {
      throw new Error('신고 거부에 실패했습니다.');
    }

    return response.json();
  },

  // 신고 삭제 (관리자용)
  deleteReport: async (id: number): Promise<ReportResponse> => {
    const response = await fetch(`${API_BASE_URL}/reports/${id}`, {
      method: 'DELETE',
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error('신고 삭제에 실패했습니다.');
    }

    return response.json();
  },
};
