import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.scss';

const Header: React.FC = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [nickname, setNickname] = useState('');

  useEffect(() => {
    // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
    const userId = localStorage.getItem('userId');
    const userNickname = localStorage.getItem('nickname');

    if (userId) {
      setIsLoggedIn(true);
      setNickname(userNickname || '');
    }
  }, []);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Í≤ÄÏÉâ:', searchQuery);
    // TODO: Í≤ÄÏÉâ Í∏∞Îä• Íµ¨ÌòÑ
  };

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('username');
    localStorage.removeItem('nickname');
    setIsLoggedIn(false);
    setNickname('');
    navigate('/');
  };

  return (
    <header className="main-header">
      <div className="header-container">
        <div className="logo-section">
          <div className="logo-image" onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
            {/* TODO: Î°úÍ≥† Ïù¥ÎØ∏ÏßÄ Ï∂îÍ∞Ä */}
            <span className="logo-text">„Éù„É™</span>
          </div>
        </div>

        <div className="title-section">
          <h1 className="site-title" onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
            GLOBALIN
          </h1>
          <p className="site-subtitle">ÏùºÎ≥∏ Ïú†ÌïôÏÉù Ïª§ÎÆ§ÎãàÌã∞</p>
        </div>

        <div className="search-section">
          <form onSubmit={handleSearch} className="search-form">
            <input
              type="text"
              placeholder="ÏÜå Í≤åÏãúÎ¨º Í≤ÄÏÉâ..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button type="submit" className="search-button">
              üîç
            </button>
          </form>
        </div>

        <div className="auth-section">
          {isLoggedIn ? (
            <div className="user-info">
              <span className="user-nickname">{nickname}Îãò</span>
              <button className="logout-button" onClick={handleLogout}>
                Î°úÍ∑∏ÏïÑÏõÉ
              </button>
            </div>
          ) : (
            <div className="auth-buttons">
              <button className="login-button" onClick={() => navigate('/login')}>
                Î°úÍ∑∏Ïù∏
              </button>
              <button className="register-button" onClick={() => navigate('/register')}>
                ÌöåÏõêÍ∞ÄÏûÖ
              </button>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
