import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { commentApi, Comment } from '../../../api/comment';
import './CommentSection.scss';

interface CommentSectionProps {
  postId: number;
}

const CommentSection: React.FC<CommentSectionProps> = ({ postId }) => {
  const navigate = useNavigate();
  const [comments, setComments] = useState<Comment[]>([]);
  const [newComment, setNewComment] = useState('');
  const [replyTo, setReplyTo] = useState<number | null>(null);
  const [replyContent, setReplyContent] = useState('');
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editContent, setEditContent] = useState('');
  const [isAnonymous, setIsAnonymous] = useState(false);

  const currentUserId = localStorage.getItem('userId');

  useEffect(() => {
    loadComments();
  }, [postId]);

  const loadComments = async () => {
    try {
      const data = await commentApi.getCommentsByPost(postId);
      setComments(data);
    } catch (err) {
      console.error('댓글 로드 실패:', err);
    }
  };

  const handleSubmitComment = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!newComment.trim()) {
      alert('댓글 내용을 입력해주세요.');
      return;
    }

    try {
      const response = await commentApi.createComment({
        postId,
        content: newComment,
        isAnonymous,
      });

      if (response.success) {
        setNewComment('');
        setIsAnonymous(false);
        loadComments();
      } else {
        alert(response.message);
      }
    } catch (err) {
      alert('댓글 작성 중 오류가 발생했습니다.');
      console.error(err);
    }
  };

  const handleSubmitReply = async (parentId: number) => {
    if (!replyContent.trim()) {
      alert('답글 내용을 입력해주세요.');
      return;
    }

    try {
      const response = await commentApi.createComment({
        postId,
        parentId,
        content: replyContent,
        isAnonymous,
      });

      if (response.success) {
        setReplyTo(null);
        setReplyContent('');
        setIsAnonymous(false);
        loadComments();
      } else {
        alert(response.message);
      }
    } catch (err) {
      alert('답글 작성 중 오류가 발생했습니다.');
      console.error(err);
    }
  };

  const handleUpdateComment = async (commentId: number) => {
    if (!editContent.trim()) {
      alert('댓글 내용을 입력해주세요.');
      return;
    }

    try {
      const response = await commentApi.updateComment(commentId, {
        content: editContent,
      });

      if (response.success) {
        setEditingId(null);
        setEditContent('');
        loadComments();
      } else {
        alert(response.message);
      }
    } catch (err) {
      alert('댓글 수정 중 오류가 발생했습니다.');
      console.error(err);
    }
  };

  const handleDeleteComment = async (commentId: number) => {
    if (!window.confirm('댓글을 삭제하시겠습니까?')) return;

    try {
      const response = await commentApi.deleteComment(commentId);

      if (response.success) {
        loadComments();
      } else {
        alert(response.message);
      }
    } catch (err) {
      alert('댓글 삭제 중 오류가 발생했습니다.');
      console.error(err);
    }
  };

  const startEdit = (comment: Comment) => {
    setEditingId(comment.id);
    setEditContent(comment.content);
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditContent('');
  };

  const startReply = (commentId: number) => {
    setReplyTo(commentId);
    setReplyContent('');
  };

  const cancelReply = () => {
    setReplyTo(null);
    setReplyContent('');
  };

  // 댓글과 대댓글 분리
  const topLevelComments = comments.filter(c => !c.parentId);
  const getReplies = (parentId: number) => comments.filter(c => c.parentId === parentId);

  const isAuthor = (comment: Comment) => {
    return currentUserId && comment.authorId.toString() === currentUserId;
  };

  return (
    <div className="comment-section">
      <h3 className="comment-title">댓글 {comments.length}</h3>

      {/* 새 댓글 작성 */}
      <form onSubmit={handleSubmitComment} className="comment-form">
        <textarea
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="댓글을 입력하세요"
          rows={3}
        />
        <div className="comment-form-actions">
          <label className="anonymous-checkbox">
            <input
              type="checkbox"
              checked={isAnonymous}
              onChange={(e) => setIsAnonymous(e.target.checked)}
            />
            익명
          </label>
          <button type="submit" className="submit-button">
            댓글 작성
          </button>
        </div>
      </form>

      {/* 댓글 목록 */}
      <div className="comment-list">
        {topLevelComments.map((comment) => (
          <div key={comment.id} className="comment-item">
            <div className="comment-header">
              <span className="comment-author">{comment.author}</span>
              <span className="comment-date">
                {new Date(comment.createdAt).toLocaleString('ko-KR')}
              </span>
            </div>

            {editingId === comment.id ? (
              <div className="comment-edit-form">
                <textarea
                  value={editContent}
                  onChange={(e) => setEditContent(e.target.value)}
                  rows={3}
                />
                <div className="edit-actions">
                  <button onClick={() => handleUpdateComment(comment.id)} className="save-button">
                    저장
                  </button>
                  <button onClick={cancelEdit} className="cancel-button">
                    취소
                  </button>
                </div>
              </div>
            ) : (
              <>
                <div className="comment-content">{comment.content}</div>
                <div className="comment-actions">
                  <button onClick={() => startReply(comment.id)} className="reply-button">
                    답글
                  </button>
                  {isAuthor(comment) && (
                    <>
                      <button onClick={() => startEdit(comment)} className="edit-button">
                        수정
                      </button>
                      <button onClick={() => handleDeleteComment(comment.id)} className="delete-button">
                        삭제
                      </button>
                    </>
                  )}
                </div>
              </>
            )}

            {/* 답글 작성 폼 */}
            {replyTo === comment.id && (
              <div className="reply-form">
                <textarea
                  value={replyContent}
                  onChange={(e) => setReplyContent(e.target.value)}
                  placeholder="답글을 입력하세요"
                  rows={2}
                />
                <div className="reply-form-actions">
                  <label className="anonymous-checkbox">
                    <input
                      type="checkbox"
                      checked={isAnonymous}
                      onChange={(e) => setIsAnonymous(e.target.checked)}
                    />
                    익명
                  </label>
                  <button onClick={() => handleSubmitReply(comment.id)} className="submit-button">
                    답글 작성
                  </button>
                  <button onClick={cancelReply} className="cancel-button">
                    취소
                  </button>
                </div>
              </div>
            )}

            {/* 대댓글 목록 */}
            {getReplies(comment.id).map((reply) => (
              <div key={reply.id} className="comment-item reply">
                <div className="comment-header">
                  <span className="comment-author">{reply.author}</span>
                  <span className="comment-date">
                    {new Date(reply.createdAt).toLocaleString('ko-KR')}
                  </span>
                </div>

                {editingId === reply.id ? (
                  <div className="comment-edit-form">
                    <textarea
                      value={editContent}
                      onChange={(e) => setEditContent(e.target.value)}
                      rows={2}
                    />
                    <div className="edit-actions">
                      <button onClick={() => handleUpdateComment(reply.id)} className="save-button">
                        저장
                      </button>
                      <button onClick={cancelEdit} className="cancel-button">
                        취소
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="comment-content">{reply.content}</div>
                    {isAuthor(reply) && (
                      <div className="comment-actions">
                        <button onClick={() => startEdit(reply)} className="edit-button">
                          수정
                        </button>
                        <button onClick={() => handleDeleteComment(reply.id)} className="delete-button">
                          삭제
                        </button>
                      </div>
                    )}
                  </>
                )}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CommentSection;
