import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { postApi } from '../../../api/post';
import './PostCreatePage.scss';

const PostCreatePage: React.FC = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const boardIdFromUrl = searchParams.get('boardId');

  const [formData, setFormData] = useState({
    boardId: boardIdFromUrl ? Number(boardIdFromUrl) : 1, // URL에서 받은 boardId 사용
    title: '',
    content: '',
    isAnonymous: false,
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [images, setImages] = useState<File[]>([]);
  const [imagePreviews, setImagePreviews] = useState<string[]>([]);
  const [uploadingImages, setUploadingImages] = useState(false);

  const boards = [
    { id: 1, name: '중고장터' },
    { id: 2, name: '자유게시판' },
    { id: 3, name: '이벤트게시판' },
    { id: 4, name: '질문게시판' },
    { id: 5, name: '인기글' },
  ];

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;

    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : type === 'select-one' ? Number(value) : value,
    }));
  };

  const handleImageSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (!files) return;

    const selectedFiles = Array.from(files);

    // 최대 5개까지만 허용
    if (images.length + selectedFiles.length > 5) {
      alert('이미지는 최대 5개까지 업로드 가능합니다.');
      return;
    }

    // 각 파일 크기 체크 (10MB)
    const invalidFiles = selectedFiles.filter(file => file.size > 10 * 1024 * 1024);
    if (invalidFiles.length > 0) {
      alert('파일 크기는 10MB 이하여야 합니다.');
      return;
    }

    // 이미지 파일만 허용
    const imageFiles = selectedFiles.filter(file => file.type.startsWith('image/'));
    if (imageFiles.length !== selectedFiles.length) {
      alert('이미지 파일만 업로드 가능합니다.');
      return;
    }

    // 이미지 미리보기 생성
    imageFiles.forEach(file => {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreviews(prev => [...prev, reader.result as string]);
      };
      reader.readAsDataURL(file);
    });

    setImages(prev => [...prev, ...imageFiles]);
  };

  const handleImageRemove = (index: number) => {
    setImages(prev => prev.filter((_, i) => i !== index));
    setImagePreviews(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');

    // 유효성 검증
    if (!formData.title.trim()) {
      setError('제목을 입력해주세요.');
      return;
    }
    if (!formData.content.trim()) {
      setError('내용을 입력해주세요.');
      return;
    }

    setLoading(true);

    try {
      const response = await postApi.createPost(formData);

      if (response.success) {
        alert(response.message);
        navigate(`/posts/${response.postId}`);
      } else {
        setError(response.message);
      }
    } catch (err) {
      setError('게시글 작성 중 오류가 발생했습니다.');
      console.error('게시글 작성 오류:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="post-create-page">
      <div className="post-create-container">
        <h1 className="page-title">게시글 작성</h1>

        <form onSubmit={handleSubmit} className="post-create-form">
          {!boardIdFromUrl && (
            <div className="form-group">
              <label htmlFor="boardId">게시판</label>
              <select
                id="boardId"
                name="boardId"
                value={formData.boardId}
                onChange={handleChange}
                required
              >
                {boards.map((board) => (
                  <option key={board.id} value={board.id}>
                    {board.name}
                  </option>
                ))}
              </select>
            </div>
          )}
          {boardIdFromUrl && (
            <div className="form-group">
              <label>게시판</label>
              <div className="selected-board">
                {boards.find(b => b.id === formData.boardId)?.name}
              </div>
            </div>
          )}

          <div className="form-group">
            <label htmlFor="title">제목</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="제목을 입력하세요"
              required
              maxLength={200}
            />
          </div>

          <div className="form-group">
            <label htmlFor="content">내용</label>
            <textarea
              id="content"
              name="content"
              value={formData.content}
              onChange={handleChange}
              placeholder="내용을 입력하세요"
              required
              rows={15}
            />
          </div>

          <div className="form-group checkbox-group">
            <label>
              <input
                type="checkbox"
                name="isAnonymous"
                checked={formData.isAnonymous}
                onChange={handleChange}
              />
              익명으로 작성
            </label>
          </div>

          {error && <div className="error-message">{error}</div>}

          <div className="button-group">
            <button type="button" className="cancel-button" onClick={() => navigate(-1)} disabled={loading}>
              취소
            </button>
            <button type="submit" className="submit-button" disabled={loading}>
              {loading ? '작성 중...' : '작성 완료'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PostCreatePage;
