import React, { useState, useEffect, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Client, over } from 'stompjs';
import { ChatMessage } from '../../types';
import './ChatWindow.scss';

interface ChatWindowProps {
  isOpen: boolean;
  onClose: () => void;
}

const ChatWindow: React.FC<ChatWindowProps> = ({ isOpen, onClose }) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [messageInput, setMessageInput] = useState('');
  const [username, setUsername] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [isJoined, setIsJoined] = useState(false);
  const stompClientRef = useRef<Client | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // 메시지 영역 자동 스크롤
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // 로그인된 사용자 정보 가져오기
  useEffect(() => {
    const loggedInUsername = localStorage.getItem('username');
    const loggedInNickname = localStorage.getItem('nickname');

    if (loggedInNickname) {
      setUsername(loggedInNickname);
    } else if (loggedInUsername) {
      setUsername(loggedInUsername);
    }
  }, []);

  // WebSocket 연결
  const connect = () => {
    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8080';
    const socket = new SockJS(`${apiUrl}/ws-chat`);
    const client = over(socket);

    client.connect(
      {},
      () => {
        setIsConnected(true);
        console.log('WebSocket 연결 성공');

        // 메시지 구독
        client.subscribe('/topic/messages', (message) => {
          const receivedMessage: ChatMessage = JSON.parse(message.body);
          setMessages((prev) => [...prev, receivedMessage]);
        });

        // 로그인된 사용자라면 자동 입장
        if (username) {
          autoJoinChat(client);
        }
      },
      (error: any) => {
        console.error('WebSocket 연결 실패:', error);
        setIsConnected(false);
      }
    );

    stompClientRef.current = client;
  };

  // WebSocket 연결 해제
  const disconnect = () => {
    if (stompClientRef.current && isConnected) {
      stompClientRef.current.disconnect(() => {
        console.log('WebSocket 연결 해제');
        setIsConnected(false);
      });
    }
  };

  // 자동 채팅방 입장 (로그인된 사용자)
  const autoJoinChat = (client: Client) => {
    if (username) {
      const joinMessage = {
        username: username,
        message: '',
      };

      client.send('/app/chat.addUser', {}, JSON.stringify(joinMessage));
      setIsJoined(true);
    }
  };

  // 수동 채팅방 입장 (비로그인 사용자)
  const joinChat = () => {
    if (!username.trim()) {
      alert('닉네임을 입력해주세요!');
      return;
    }

    if (stompClientRef.current && isConnected) {
      const joinMessage = {
        username: username.trim(),
        message: '',
      };

      stompClientRef.current.send('/app/chat.addUser', {}, JSON.stringify(joinMessage));
      setIsJoined(true);
    }
  };

  // 메시지 전송
  const sendMessage = () => {
    if (!messageInput.trim()) return;

    if (stompClientRef.current && isConnected && isJoined) {
      const chatMessage = {
        username: username,
        message: messageInput.trim(),
      };

      stompClientRef.current.send('/app/chat.sendMessage', {}, JSON.stringify(chatMessage));
      setMessageInput('');
    }
  };

  // 엔터키로 메시지 전송
  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      if (isJoined) {
        sendMessage();
      } else {
        joinChat();
      }
    }
  };

  // 컴포넌트 마운트 시 WebSocket 연결
  useEffect(() => {
    if (isOpen) {
      connect();

      // 기존 메시지 불러오기
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8080';
      fetch(`${apiUrl}/api/chat/recent?limit=50`)
        .then((res) => res.json())
        .then((data: ChatMessage[]) => {
          setMessages(data.reverse()); // 시간순 정렬
        })
        .catch((error) => console.error('메시지 로딩 실패:', error));
    }

    return () => {
      disconnect();
    };
  }, [isOpen, username]);

  if (!isOpen) return null;

  return (
    <div className="chat-window">
      <div className="chat-window__header">
        <h3 className="chat-window__title">전체 채팅방</h3>
        <button className="chat-window__close" onClick={onClose}>
          ✕
        </button>
      </div>

      {!isJoined ? (
        <div className="chat-window__join">
          {username ? (
            <div className="chat-window__connecting">
              <p>채팅방 연결 중...</p>
              <p className="chat-window__username-display">{username}님으로 입장합니다</p>
            </div>
          ) : (
            <>
              <h4>로그인이 필요합니다</h4>
              <div className="chat-window__join-form">
                <input
                  type="text"
                  placeholder="닉네임 입력 (임시)"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  onKeyPress={handleKeyPress}
                  className="chat-window__username-input"
                />
                <button onClick={joinChat} className="chat-window__join-button">
                  입장
                </button>
              </div>
              <p className="chat-window__login-notice">
                <a href="/login">로그인</a>하시면 자동으로 입장됩니다
              </p>
            </>
          )}
        </div>
      ) : (
        <>
          <div className="chat-window__user-info">
            <span className="chat-window__current-user">{username}님으로 채팅 중</span>
          </div>

          <div className="chat-window__messages">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`chat-message ${
                  msg.username === 'System' ? 'chat-message--system' : ''
                } ${msg.username === username ? 'chat-message--own' : ''}`}
              >
                <span className="chat-message__username">{msg.username}</span>
                <span className="chat-message__text">{msg.message}</span>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          <div className="chat-window__input">
            <input
              type="text"
              placeholder="메시지를 입력하세요..."
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              onKeyPress={handleKeyPress}
              className="chat-window__message-input"
            />
            <button onClick={sendMessage} className="chat-window__send-button">
              전송
            </button>
          </div>
        </>
      )}

      <div className="chat-window__status">
        {isConnected ? (
          <span className="chat-window__status--connected">● 연결됨</span>
        ) : (
          <span className="chat-window__status--disconnected">● 연결 끊김</span>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
