import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { UserProfileDetail, UpdateProfileRequest } from '../../../types';
import './ProfileEditPage.scss';

const ProfileEditPage: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [profile, setProfile] = useState<UserProfileDetail | null>(null);

  const [formData, setFormData] = useState<UpdateProfileRequest>({
    nickname: '',
    email: '',
    avatar: '',
    bio: ''
  });

  useEffect(() => {
    fetchProfile();
  }, [userId]);

  const fetchProfile = async () => {
    setLoading(true);
    setError('');

    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8080';
      const response = await fetch(`${apiUrl}/api/users/${userId}/profile`);

      if (!response.ok) {
        throw new Error('프로필을 불러올 수 없습니다.');
      }

      const data = await response.json();
      setProfile(data);

      // 폼 초기값 설정
      setFormData({
        nickname: data.nickname || '',
        email: data.email || '',
        avatar: data.avatar || '',
        bio: data.bio || ''
      });
    } catch (err) {
      console.error('프로필 조회 오류:', err);
      setError('프로필을 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // 유효성 검사
    if (!formData.nickname?.trim()) {
      setError('닉네임을 입력해주세요.');
      return;
    }

    if (!formData.email?.trim()) {
      setError('이메일을 입력해주세요.');
      return;
    }

    // 이메일 형식 검사
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('올바른 이메일 형식을 입력해주세요.');
      return;
    }

    setSaving(true);
    setError('');

    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8080';
      const response = await fetch(`${apiUrl}/api/users/${userId}/profile`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || '프로필 업데이트에 실패했습니다.');
      }

      // 성공하면 프로필 페이지로 이동
      navigate(`/profile/${userId}`);
    } catch (err: any) {
      console.error('프로필 업데이트 오류:', err);
      setError(err.message || '프로필 업데이트 중 오류가 발생했습니다.');
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    navigate(`/profile/${userId}`);
  };

  if (loading) {
    return <div className="profile-edit-page"><div className="loading">로딩 중...</div></div>;
  }

  if (!profile) {
    return (
      <div className="profile-edit-page">
        <div className="error">프로필을 찾을 수 없습니다.</div>
      </div>
    );
  }

  // 본인 확인 (localStorage의 userId와 비교)
  const currentUserId = localStorage.getItem('userId');
  if (currentUserId !== userId) {
    return (
      <div className="profile-edit-page">
        <div className="error">본인의 프로필만 수정할 수 있습니다.</div>
      </div>
    );
  }

  return (
    <div className="profile-edit-page">
      <div className="edit-container">
        <h1>프로필 편집</h1>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="edit-form">
          <div className="form-group">
            <label htmlFor="nickname">닉네임</label>
            <input
              type="text"
              id="nickname"
              name="nickname"
              value={formData.nickname}
              onChange={handleInputChange}
              placeholder="닉네임을 입력하세요"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">이메일</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="이메일을 입력하세요"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="avatar">아바타 URL</label>
            <input
              type="url"
              id="avatar"
              name="avatar"
              value={formData.avatar}
              onChange={handleInputChange}
              placeholder="아바타 이미지 URL (선택사항)"
            />
            {formData.avatar && (
              <div className="avatar-preview">
                <img src={formData.avatar} alt="미리보기" />
              </div>
            )}
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-save" disabled={saving}>
              {saving ? '저장 중...' : '저장'}
            </button>
            <button type="button" className="btn-cancel" onClick={handleCancel} disabled={saving}>
              취소
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProfileEditPage;
