const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

export interface Comment {
  id: number;
  postId: number;
  authorId: number;
  parentId: number | null;
  content: string;
  createdAt: number;
  updatedAt?: number;
  status: string;
  isAnonymous: boolean;
  author: string;
  likeCount: number;
}

export interface CreateCommentRequest {
  postId: number;
  parentId?: number | null;
  content: string;
  isAnonymous?: boolean;
}

export interface UpdateCommentRequest {
  content: string;
}

export interface CommentResponse {
  success: boolean;
  message: string;
  commentId?: number;
}

export const commentApi = {
  // 댓글 작성
  createComment: async (data: CreateCommentRequest): Promise<CommentResponse> => {
    const response = await fetch(`${API_URL}/api/comments`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(data),
    });

    return await response.json();
  },

  // 게시글의 모든 댓글 조회
  getCommentsByPost: async (postId: number): Promise<Comment[]> => {
    const response = await fetch(`${API_URL}/api/comments/post/${postId}`, {
      credentials: 'include',
    });

    return await response.json();
  },

  // 댓글 수정
  updateComment: async (commentId: number, data: UpdateCommentRequest): Promise<CommentResponse> => {
    const response = await fetch(`${API_URL}/api/comments/${commentId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(data),
    });

    return await response.json();
  },

  // 댓글 삭제
  deleteComment: async (commentId: number): Promise<CommentResponse> => {
    const response = await fetch(`${API_URL}/api/comments/${commentId}`, {
      method: 'DELETE',
      credentials: 'include',
    });

    return await response.json();
  },
};
