import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { authApi, RegisterRequest } from '../../../api/auth';
import './RegisterPage.scss';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

const RegisterPage: React.FC = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1); // 1: 이메일 인증, 2: 회원가입 완료
  const [formData, setFormData] = useState<RegisterRequest>({
    username: '',
    password: '',
    email: '',
    nickname: '',
  });
  const [confirmPassword, setConfirmPassword] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [codeSent, setCodeSent] = useState(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // 1단계: 이메일 인증 코드 전송
  const handleSendCode = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');

    if (!formData.email.trim()) {
      setError('이메일을 입력해주세요.');
      return;
    }

    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/api/auth/send-register-verification-code`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          email: formData.email,
        }),
      });

      const data = await response.json();

      if (data.success) {
        alert(data.message);
        setCodeSent(true);
        setStep(2);
      } else {
        setError(data.message);
      }
    } catch (err) {
      console.error('인증 코드 전송 오류:', err);
      setError('인증 코드 전송 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  // 2단계: 회원가입 완료
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');

    // 클라이언트 측 유효성 검증
    if (!verificationCode.trim()) {
      setError('인증 코드를 입력해주세요.');
      return;
    }

    if (formData.password !== confirmPassword) {
      setError('비밀번호가 일치하지 않습니다.');
      return;
    }

    if (formData.username.length < 4 || formData.username.length > 20) {
      setError('아이디는 4-20자 사이여야 합니다.');
      return;
    }

    if (formData.password.length < 8) {
      setError('비밀번호는 최소 8자 이상이어야 합니다.');
      return;
    }

    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/api/auth/register-with-verification`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          ...formData,
          verificationCode: verificationCode,
        }),
      });

      const data = await response.json();

      if (data.success) {
        alert(data.message);
        navigate('/login');
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError('서버와의 통신 중 오류가 발생했습니다.');
      console.error('회원가입 오류:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <h1 className="register-title">회원가입</h1>
        <p className="register-subtitle">Globalin에 오신 것을 환영합니다</p>

        {step === 1 && (
          <>
            <p className="description">이메일을 입력하고 인증 코드를 받으세요.</p>
            <form onSubmit={handleSendCode} className="register-form">
              <div className="form-group">
                <label htmlFor="email">이메일</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="example@example.com"
                  required
                  disabled={loading}
                />
              </div>

              {error && <div className="error-message">{error}</div>}

              <button type="submit" className="register-button" disabled={loading}>
                {loading ? '전송 중...' : '인증 코드 받기'}
              </button>
            </form>
          </>
        )}

        {step === 2 && (
          <>
            <p className="description">이메일로 전송된 인증 코드를 입력하고 회원정보를 작성하세요.</p>
            <div className="info-box">
              <p><strong>이메일:</strong> {formData.email}</p>
            </div>
            <form onSubmit={handleSubmit} className="register-form">
              <div className="form-group">
                <label htmlFor="verificationCode">인증 코드</label>
                <input
                  type="text"
                  id="verificationCode"
                  name="verificationCode"
                  value={verificationCode}
                  onChange={(e) => setVerificationCode(e.target.value)}
                  placeholder="6자리 숫자 입력"
                  required
                  maxLength={6}
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="username">아이디</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleChange}
                  placeholder="4-20자 영문, 숫자"
                  required
                  minLength={4}
                  maxLength={20}
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="password">비밀번호</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="최소 8자 이상"
                  required
                  minLength={8}
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="confirmPassword">비밀번호 확인</label>
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  placeholder="비밀번호를 다시 입력하세요"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="nickname">닉네임</label>
                <input
                  type="text"
                  id="nickname"
                  name="nickname"
                  value={formData.nickname}
                  onChange={handleChange}
                  placeholder="사용할 닉네임을 입력하세요"
                  required
                  maxLength={50}
                  disabled={loading}
                />
              </div>

              {error && <div className="error-message">{error}</div>}

              <div className="button-group">
                <button
                  type="button"
                  className="back-button"
                  onClick={() => {
                    setStep(1);
                    setCodeSent(false);
                  }}
                  disabled={loading}
                >
                  이전
                </button>
                <button type="submit" className="register-button" disabled={loading}>
                  {loading ? '처리 중...' : '회원가입'}
                </button>
              </div>
            </form>
          </>
        )}

        <div className="login-link">
          이미 계정이 있으신가요?{' '}
          <a href="/login" onClick={(e) => { e.preventDefault(); navigate('/login'); }}>
            로그인
          </a>
        </div>
      </div>
    </div>
  );
