import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { lectureReviewApi, LectureReview } from '../../../api/lectureReview';
import './LectureReviewWritePage.scss';

const LectureReviewWritePage: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<LectureReview>({
    courseName: '',
    professor: '',
    difficulty: 3,
    workload: 3,
    satisfaction: 3,
    reviewText: '',
    semester: '',
    year: new Date().getFullYear(),
    isAnonymous: false,
  });
  const [error, setError] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.courseName.trim()) {
      setError('강의명을 입력해주세요.');
      return;
    }
    if (!formData.professor.trim()) {
      setError('교수님 성함을 입력해주세요.');
      return;
    }

    setSubmitting(true);
    setError('');

    try {
      await lectureReviewApi.createReview(formData);
      alert('강의평이 작성되었습니다.');
      navigate('/lecture-reviews');
    } catch (err: any) {
      setError(err.message || '강의평 작성에 실패했습니다.');
    } finally {
      setSubmitting(false);
    }
  };

  const renderStarInput = (label: string, value: number, onChange: (rating: number) => void) => {
    return (
      <div className="star-input">
        <label>{label}</label>
        <div className="stars-container">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={star <= value ? 'star filled' : 'star'}
              onClick={() => onChange(star)}
            >
              ★
            </span>
          ))}
          <span className="rating-value">{value}</span>
        </div>
      </div>
    );
  };

  return (
    <div className="review-write-page">
      <div className="write-container">
        <div className="write-header">
          <h1>강의평 작성</h1>
          <button className="back-btn" onClick={() => navigate('/lecture-reviews')}>
            취소
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="write-form">
          <div className="form-section">
            <h3>강의 정보</h3>
            <div className="form-group">
              <label>강의명 *</label>
              <input
                type="text"
                value={formData.courseName}
                onChange={(e) => setFormData({ ...formData, courseName: e.target.value })}
                placeholder="예: 자바 프로그래밍"
                required
              />
            </div>

            <div className="form-group">
              <label>교수님 *</label>
              <input
                type="text"
                value={formData.professor}
                onChange={(e) => setFormData({ ...formData, professor: e.target.value })}
                placeholder="예: 김교수"
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>수강 연도</label>
                <input
                  type="number"
                  value={formData.year}
                  onChange={(e) => setFormData({ ...formData, year: parseInt(e.target.value) })}
                  min="2000"
                  max="2099"
                />
              </div>

              <div className="form-group">
                <label>학기</label>
                <select
                  value={formData.semester}
                  onChange={(e) => setFormData({ ...formData, semester: e.target.value })}
                >
                  <option value="">선택</option>
                  <option value="1학기">1학기</option>
                  <option value="2학기">2학기</option>
                  <option value="여름학기">여름학기</option>
                  <option value="겨울학기">겨울학기</option>
                </select>
              </div>
            </div>
          </div>

          <div className="form-section">
            <h3>평점 (1-5점)</h3>
            {renderStarInput('난이도', formData.difficulty, (rating) =>
              setFormData({ ...formData, difficulty: rating })
            )}
            {renderStarInput('과제량', formData.workload, (rating) =>
              setFormData({ ...formData, workload: rating })
            )}
            {renderStarInput('만족도', formData.satisfaction, (rating) =>
              setFormData({ ...formData, satisfaction: rating })
            )}
          </div>

          <div className="form-section">
            <h3>리뷰</h3>
            <div className="form-group">
              <label>상세 리뷰 (선택)</label>
              <textarea
                value={formData.reviewText}
                onChange={(e) => setFormData({ ...formData, reviewText: e.target.value })}
                placeholder="강의에 대한 자세한 후기를 작성해주세요..."
                rows={6}
              />
            </div>

            <div className="form-group checkbox">
              <label>
                <input
                  type="checkbox"
                  checked={formData.isAnonymous}
                  onChange={(e) => setFormData({ ...formData, isAnonymous: e.target.checked })}
                />
                익명으로 작성
              </label>
            </div>
          </div>

          <div className="form-actions">
            <button type="button" className="cancel-btn" onClick={() => navigate('/lecture-reviews')}>
              취소
            </button>
            <button type="submit" className="submit-btn" disabled={submitting}>
              {submitting ? '작성 중...' : '작성 완료'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LectureReviewWritePage;
