    try {
      const data = await adminApi.getAllUsers();
      setUsers(data);
    } catch (err: any) {
      alert(err.message || 'ÏÇ¨Ïö©Ïûê Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const loadPosts = async () => {
    try {
      const data = await adminApi.getAllPosts();
      setPosts(data);
    } catch (err: any) {
      alert(err.message || 'Í≤åÏãúÍ∏Ä Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const handleUserStatusChange = async (userId: number, newStatus: string) => {
    if (!window.confirm('ÏÇ¨Ïö©Ïûê ÏÉÅÌÉúÎ•º Î≥ÄÍ≤ΩÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) return;

    try {
      await adminApi.updateUserStatus(userId, newStatus);
      alert('ÏÇ¨Ïö©Ïûê ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.');
      loadUsers();
    } catch (err: any) {
      alert(err.message || 'ÏÉÅÌÉú Î≥ÄÍ≤ΩÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const handleDeletePost = async (postId: number) => {
    if (!window.confirm('Í≤åÏãúÍ∏ÄÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) return;

    try {
      await adminApi.deletePost(postId);
      alert('Í≤åÏãúÍ∏ÄÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
      loadPosts();
    } catch (err: any) {
      alert(err.message || 'Í≤åÏãúÍ∏Ä ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  if (loading) {
    return (
      <div className="admin-page">
        <div className="loading">Î°úÎî© Ï§ë...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-page">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-header">
        <h1>Í¥ÄÎ¶¨Ïûê ÌéòÏù¥ÏßÄ</h1>
      </div>

      <div className="admin-tabs">
        <button
          className={`tab-button ${activeTab === 'dashboard' ? 'active' : ''}`}
          onClick={() => setActiveTab('dashboard')}
        >
          ÎåÄÏãúÎ≥¥Îìú
        </button>
        <button
          className={`tab-button ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          ÏÇ¨Ïö©Ïûê Í¥ÄÎ¶¨
        </button>
        <button
          className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
          onClick={() => setActiveTab('posts')}
        >
          Í≤åÏãúÍ∏Ä Í¥ÄÎ¶¨
        </button>
      </div>

      <div className="admin-content">
        {activeTab === 'dashboard' && stats && (
          <div className="dashboard">
            <h2>ÏãúÏä§ÌÖú ÌÜµÍ≥Ñ</h2>
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon">üë•</div>
                <div className="stat-info">
                  <div className="stat-value">{stats.totalUsers}</div>
                  <div className="stat-label">Ï†ÑÏ≤¥ ÏÇ¨Ïö©Ïûê</div>
                </div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">üìù</div>
                <div className="stat-info">
                  <div className="stat-value">{stats.totalPosts}</div>
                  <div className="stat-label">Ï†ÑÏ≤¥ Í≤åÏãúÍ∏Ä</div>
                </div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">üí¨</div>
                <div className="stat-info">
                  <div className="stat-value">{stats.totalComments}</div>
                  <div className="stat-label">Ï†ÑÏ≤¥ ÎåìÍ∏Ä</div>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'users' && (
          <div className="users-management">
            <h2>ÏÇ¨Ïö©Ïûê Í¥ÄÎ¶¨</h2>
            <p className="coming-soon">ÏÇ¨Ïö©Ïûê Í¥ÄÎ¶¨ Í∏∞Îä• Í∞úÎ∞ú Ï§ë...</p>
          </div>
        )}

        {activeTab === 'posts' && (
          <div className="posts-management">
            <h2>Í≤åÏãúÍ∏Ä Í¥ÄÎ¶¨</h2>
            <p className="coming-soon">Í≤åÏãúÍ∏Ä Í¥ÄÎ¶¨ Í∏∞Îä• Í∞úÎ∞ú Ï§ë...</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
