import React, { useState, useEffect } from 'react';
import { adminApi, AdminStats } from '../../../api/admin';
import './AdminPage.scss';

interface User {
  id: number;
  username: string;
  email: string;
  nickname: string;
  status: string;
  role: string;
  joinDate: string;
}

interface Post {
  id: number;
  title: string;
  author: string;
