import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { UserProfileDetail } from '../../../types';
import './ProfilePage.scss';

const ProfilePage: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<UserProfileDetail | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProfile();
  }, [userId]);

  const fetchProfile = async () => {
    setLoading(true);
    setError('');

    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8080';
      const response = await fetch(`${apiUrl}/api/users/${userId}/profile`);

      if (!response.ok) {
        throw new Error('프로필을 불러올 수 없습니다.');
      }

      const data = await response.json();
      setProfile(data);
    } catch (err) {
      console.error('프로필 조회 오류:', err);
      setError('프로필을 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR');
  };

  if (loading) {
    return <div className="profile-page"><div className="loading">로딩 중...</div></div>;
  }

  if (error || !profile) {
    return (
      <div className="profile-page">
        <div className="error">{error || '프로필을 찾을 수 없습니다.'}</div>
      </div>
    );
  }

  // 본인 프로필인지 확인
  const currentUserId = localStorage.getItem('userId');
  const isOwnProfile = currentUserId === userId;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <div className="profile-avatar">
          {profile.avatar ? (
            <img src={profile.avatar} alt={profile.nickname} />
          ) : (
            <div className="avatar-placeholder">{profile.nickname?.charAt(0) || 'U'}</div>
          )}
        </div>
        <div className="profile-info">
          <h1 className="profile-nickname">{profile.nickname}</h1>
          <p className="profile-username">@{profile.username}</p>
          <p className="profile-email">{profile.email}</p>
          <p className="profile-join-date">가입일: {formatDate(profile.joinDate)}</p>
          {isOwnProfile && (
            <button className="btn-edit-profile" onClick={() => navigate(`/profile/${userId}/edit`)}>
              프로필 편집
            </button>
          )}
        </div>
      </div>

      {profile.bio && (
        <div className="profile-bio">
          <h2>소개</h2>
          <p>{profile.bio}</p>
        </div>
      )}

      <div className="profile-stats">
        <div className="stat-item">
          <div className="stat-value">{profile.postCount || 0}</div>
          <div className="stat-label">작성 글</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{profile.commentCount || 0}</div>
          <div className="stat-label">작성 댓글</div>
        </div>
      </div>

      <div className="profile-content">
        <section className="recent-posts">
          <h2>최근 작성 글</h2>
          {profile.recentPosts && profile.recentPosts.length > 0 ? (
            <ul className="post-list">
              {profile.recentPosts.map((post) => (
                <li key={post.id} className="post-item" onClick={() => navigate(`/post/${post.id}`)}>
                  <h3 className="post-title">{post.title}</h3>
                  <div className="post-meta">
                    <span>{post.boardName}</span>
                    <span>조회 {post.viewCount}</span>
                    <span>댓글 {post.commentCount}</span>
                    <span className="post-date">{formatDate(post.createdAt)}</span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p className="empty-message">작성한 글이 없습니다.</p>
          )}
        </section>

        <section className="recent-comments">
          <h2>최근 작성 댓글</h2>
          {profile.recentComments && profile.recentComments.length > 0 ? (
            <ul className="comment-list">
              {profile.recentComments.map((comment) => (
                <li key={comment.id} className="comment-item">
                  <p className="comment-content">{comment.content}</p>
                  <div className="comment-meta">
                    <span className="comment-date">{formatDate(comment.createdAt)}</span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p className="empty-message">작성한 댓글이 없습니다.</p>
          )}
        </section>
      </div>
    </div>
  );
};

export default ProfilePage;
