import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './FindUsernamePage.scss';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

const FindUsernamePage: React.FC = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<{ success: boolean; message: string; username?: string } | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email.trim()) {
      alert('이메일을 입력해주세요.');
      return;
    }

    setLoading(true);
    setResult(null);

    try {
      const response = await fetch(`${API_URL}/api/auth/find-username`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ email }),
      });

      const data = await response.json();
      setResult(data);

    } catch (err) {
      console.error('아이디 찾기 오류:', err);
      setResult({
        success: false,
        message: '아이디 찾기 중 오류가 발생했습니다.',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="find-username-page">
      <div className="find-username-container">
        <h1>아이디 찾기</h1>
        <p className="description">가입 시 등록한 이메일을 입력해주세요.</p>

        {!result && (
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>이메일</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="example@email.com"
                disabled={loading}
              />
            </div>

            <button type="submit" className="submit-button" disabled={loading}>
              {loading ? '검색 중...' : '아이디 찾기'}
            </button>
          </form>
        )}

        {result && (
          <div className={`result ${result.success ? 'success' : 'error'}`}>
            <div className="result-icon">
              {result.success ? '✓' : '✗'}
            </div>
            <p className="result-message">{result.message}</p>
            {result.success && result.username && (
              <div className="username-box">
                <strong>아이디:</strong> {result.username}
              </div>
            )}
            <div className="result-buttons">
              {result.success ? (
                <>
                  <button onClick={() => navigate('/login')} className="login-button">
                    로그인하기
                  </button>
                  <button onClick={() => navigate('/find-password')} className="find-password-button">
                    비밀번호 찾기
                  </button>
                </>
              ) : (
                <button onClick={() => setResult(null)} className="retry-button">
                  다시 시도
                </button>
              )}
            </div>
          </div>
        )}

        <div className="links">
          <button onClick={() => navigate('/login')}>로그인</button>
          <span>|</span>
          <button onClick={() => navigate('/find-password')}>비밀번호 찾기</button>
          <span>|</span>
          <button onClick={() => navigate('/register')}>회원가입</button>
        </div>
      </div>
    </div>
  );
};

export default FindUsernamePage;
