import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './FindPasswordPage.scss';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

const FindPasswordPage: React.FC = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1); // 1: 이메일/아이디 입력, 2: 인증코드 입력 및 비밀번호 변경
  const [formData, setFormData] = useState({
    email: '',
    username: '',
    verificationCode: '',
    newPassword: '',
    confirmPassword: '',
  });
  const [loading, setLoading] = useState(false);
  const [codeSent, setCodeSent] = useState(false);
  const [result, setResult] = useState<{ success: boolean; message: string } | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSendCode = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.email.trim() || !formData.username.trim()) {
      alert('이메일과 아이디를 입력해주세요.');
      return;
    }

    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/api/auth/send-verification-code`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          email: formData.email,
          username: formData.username,
        }),
      });

      const data = await response.json();

      if (data.success) {
        alert(data.message);
        setCodeSent(true);
        setStep(2);
      } else {
        alert(data.message);
      }

    } catch (err) {
      console.error('인증 코드 전송 오류:', err);
      alert('인증 코드 전송 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleResetPassword = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.verificationCode.trim() || !formData.newPassword.trim() || !formData.confirmPassword.trim()) {
      alert('모든 항목을 입력해주세요.');
      return;
    }

    if (formData.newPassword !== formData.confirmPassword) {
      alert('비밀번호가 일치하지 않습니다.');
      return;
    }

    if (formData.newPassword.length < 6) {
      alert('비밀번호는 6자 이상이어야 합니다.');
      return;
    }

    setLoading(true);
    setResult(null);

    try {
      const response = await fetch(`${API_URL}/api/auth/reset-password`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          email: formData.email,
          username: formData.username,
          verificationCode: formData.verificationCode,
          newPassword: formData.newPassword,
        }),
      });

      const data = await response.json();
      setResult(data);

      if (data.success) {
        setFormData({
          email: '',
          username: '',
          verificationCode: '',
          newPassword: '',
          confirmPassword: '',
        });
      }

    } catch (err) {
      console.error('비밀번호 재설정 오류:', err);
      setResult({
        success: false,
        message: '비밀번호 재설정 중 오류가 발생했습니다.',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="find-password-page">
      <div className="find-password-container">
        <h1>비밀번호 찾기</h1>

        {step === 1 && !result && (
          <>
            <p className="description">이메일과 아이디를 입력하면 인증 코드를 전송합니다.</p>
            <form onSubmit={handleSendCode}>
              <div className="form-group">
                <label>이메일</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="example@email.com"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>아이디</label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleChange}
                  placeholder="아이디를 입력하세요"
                  disabled={loading}
                />
              </div>

              <button type="submit" className="submit-button" disabled={loading}>
                {loading ? '전송 중...' : '인증 코드 받기'}
              </button>
            </form>
          </>
        )}

        {step === 2 && !result && (
          <>
            <p className="description">이메일로 전송된 인증 코드를 입력하고 새 비밀번호를 설정하세요.</p>
            <div className="info-box">
              <p><strong>이메일:</strong> {formData.email}</p>
              <p><strong>아이디:</strong> {formData.username}</p>
            </div>
            <form onSubmit={handleResetPassword}>
              <div className="form-group">
                <label>인증 코드</label>
                <input
                  type="text"
                  name="verificationCode"
                  value={formData.verificationCode}
                  onChange={handleChange}
                  placeholder="6자리 숫자 입력"
                  disabled={loading}
                  maxLength={6}
                />
              </div>

              <div className="form-group">
                <label>새 비밀번호</label>
                <input
                  type="password"
                  name="newPassword"
                  value={formData.newPassword}
                  onChange={handleChange}
                  placeholder="6자 이상 입력하세요"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>비밀번호 확인</label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  placeholder="비밀번호를 다시 입력하세요"
                  disabled={loading}
                />
              </div>

              <div className="button-group">
                <button
                  type="button"
                  className="back-button"
                  onClick={() => {
                    setStep(1);
                    setCodeSent(false);
                  }}
                  disabled={loading}
                >
                  이전
                </button>
                <button type="submit" className="submit-button" disabled={loading}>
                  {loading ? '처리 중...' : '비밀번호 변경'}
                </button>
              </div>
            </form>
          </>
        )}

        {result && (
          <div className={`result ${result.success ? 'success' : 'error'}`}>
            <div className="result-icon">
              {result.success ? '✓' : '✗'}
            </div>
            <p className="result-message">{result.message}</p>
            <div className="result-buttons">
              {result.success ? (
                <button onClick={() => navigate('/login')} className="login-button">
                  로그인하기
                </button>
              ) : (
                <button onClick={() => {
                  setResult(null);
                  setStep(1);
                  setCodeSent(false);
                }} className="retry-button">
                  다시 시도
                </button>
              )}
            </div>
          </div>
        )}

        <div className="links">
          <button onClick={() => navigate('/login')}>로그인</button>
