import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { postApi, Post } from '../../../api/post';
import './PostListPage.scss';

const BOARD_NAMES: { [key: number]: string } = {
  1: '인문학',
  2: '자유게시판',
  3: '질문게시판',
  4: '스터디',
  5: '밥/파티',
};

const PostListPage: React.FC = () => {
  const { boardId } = useParams<{ boardId: string }>();
  const navigate = useNavigate();
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const currentBoardId = Number(boardId);
  const boardName = BOARD_NAMES[currentBoardId] || '게시판';

  useEffect(() => {
    loadPosts();
  }, [boardId]);

  const loadPosts = async () => {
    if (!boardId) return;

    try {
      const data = await postApi.getPostsByBoard(Number(boardId));
      setPosts(data);
    } catch (err) {
      setError('게시글을 불러오는 중 오류가 발생했습니다.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handlePostClick = (postId: number) => {
    navigate(`/posts/${postId}`);
  };

  const handleWriteClick = () => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      alert('로그인이 필요합니다.');
      navigate('/login');
      return;
    }
    navigate('/posts/create');
  };

  if (loading) return <div className="loading">로딩 중...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="post-list-page">
      <div className="post-list-container">
        <div className="board-header">
          <h1 className="board-title">{boardName}</h1>
          <button className="write-button" onClick={handleWriteClick}>
            글쓰기
          </button>
        </div>

        {posts.length === 0 ? (
          <div className="no-posts">
            <p>아직 게시글이 없습니다.</p>
            <p>첫 번째 게시글을 작성해보세요!</p>
          </div>
        ) : (
          <div className="post-list">
            <div className="post-list-header">
              <div className="col-title">제목</div>
              <div className="col-author">작성자</div>
              <div className="col-date">작성일</div>
              <div className="col-views">조회</div>
              <div className="col-likes">추천</div>
            </div>

            {posts.map((post) => (
              <div
                key={post.id}
                className="post-item"
                onClick={() => handlePostClick(post.id)}
              >
                <div className="col-title">
                  <span className="title-text">{post.title}</span>
                  {post.commentCount > 0 && (
                    <span className="comment-count">[{post.commentCount}]</span>
                  )}
                </div>
                <div className="col-author">{post.author}</div>
                <div className="col-date">
                  {new Date(post.createdAt).toLocaleDateString('ko-KR')}
                </div>
                <div className="col-views">{post.viewCount}</div>
                <div className="col-likes">{post.likeCount}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PostListPage;
