import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { postApi, Post } from '../../../api/post';
import { messageApi } from '../../../api/message';
import CommentSection from './CommentSection';
import './PostDetailPage.scss';

const PostDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [post, setPost] = useState<Post | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);

  const currentUserId = localStorage.getItem('userId');

  useEffect(() => {
    loadPost();
    loadLikeInfo();
  }, [id]);

  const loadPost = async () => {
    if (!id) return;

    try {
      const data = await postApi.getPost(Number(id));
      setPost(data);
    } catch (err) {
      setError('Í≤åÏãúÍ∏ÄÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadLikeInfo = async () => {
    if (!id) return;

    try {
      const [count, status] = await Promise.all([
        postApi.getLikeCount(Number(id)),
        postApi.getLikeStatus(Number(id))
      ]);
      setLikeCount(count);
      setIsLiked(status);
    } catch (err) {
      console.error('Ï¢ãÏïÑÏöî Ï†ïÎ≥¥ Î°úÎî© Ïã§Ìå®:', err);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) return;
    if (!id) return;

    try {
      const response = await postApi.deletePost(Number(id));
      if (response.success) {
        alert(response.message);
        navigate(`/board/${post?.boardId}`);
      } else {
        alert(response.message);
      }
    } catch (err) {
      alert('ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      console.error(err);
    }
  };

  const handleLike = async () => {
    if (!currentUserId) {
      alert('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
      return;
    }

    if (!id) return;

    try {
      console.log('Ï¢ãÏïÑÏöî ÏöîÏ≤≠ ÏãúÏûë:', id);
      const response = await postApi.toggleLike(Number(id));
      console.log('Ï¢ãÏïÑÏöî ÏùëÎãµ:', response);

      if (response.success) {
        // Ï¢ãÏïÑÏöî ÏÉÅÌÉú Î∞òÏ†Ñ
        setIsLiked(!isLiked);
        setLikeCount(prev => isLiked ? prev - 1 : prev + 1);
      } else {
        alert(response.message || 'Ï¢ãÏïÑÏöî Ï≤òÎ¶¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (err: any) {
      console.error('Ï¢ãÏïÑÏöî ÏóêÎü¨:', err);
      alert(err.message || 'Ï¢ãÏïÑÏöî Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const handleAuthorClick = () => {
    if (post) {
      navigate(`/profile/${post.authorId}`);
    }
  };

  if (loading) return <div className="loading">Î°úÎî© Ï§ë...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!post) return <div className="error">Í≤åÏãúÍ∏ÄÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</div>;

  const isAuthor = currentUserId && post.authorId.toString() === currentUserId;

  return (
    <div className="post-detail-page">
      <div className="post-detail-container">
        <div className="post-header">
          <div className="board-name">{post.boardName}</div>
          <h1 className="post-title">{post.title}</h1>
          <div className="post-meta">
            <span className="author author-link" onClick={handleAuthorClick}>
              {post.author}
            </span>
            <span className="separator">|</span>
            <span className="date">{new Date(post.createdAt).toLocaleString('ko-KR')}</span>
            <span className="separator">|</span>
            <span className="views">Ï°∞Ìöå {post.viewCount}</span>
          </div>
        </div>

        <div className="post-content">
          {post.content.split('\n').map((line, index) => (
            <p key={index}>{line || '\u00A0'}</p>
          ))}
        </div>

        <div className="post-stats">
          <button
            className={`like-button ${isLiked ? 'liked' : ''}`}
            onClick={handleLike}
          >
            <span className="icon">{isLiked ? '‚ù§Ô∏è' : 'ü§ç'}</span>
            <span className="count">{likeCount}</span>
          </button>
          <div className="stat">
            <span className="icon">üí¨</span>
            <span className="count">{post.commentCount}</span>
          </div>
        </div>

        <div className="button-group">
          <button className="list-button" onClick={() => navigate(`/board/${post.boardId}`)}>
            Î™©Î°ùÏúºÎ°ú
          </button>
          {isAuthor && (
            <>
              <button className="edit-button" onClick={() => navigate(`/posts/${post.id}/edit`)}>
                ÏàòÏ†ï
              </button>
              <button className="delete-button" onClick={handleDelete}>
                ÏÇ≠Ï†ú
              </button>
            </>
          )}
        </div>

        {/* ÎåìÍ∏Ä ÏÑπÏÖò */}
        <CommentSection postId={post.id} />
      </div>
    </div>
  );
};

export default PostDetailPage;
