import React, { useState, useEffect } from 'react';
import { adminApi, AdminStats } from '../../../api/admin';
import './AdminPage.scss';

interface User {
  id: number;
  username: string;
  email: string;
  nickname: string;
  status: string;
  role: string;
  joinDate: string;
}

interface Post {
  id: number;
  title: string;
  author: string;
  boardName: string;
  viewCount: number;
  createdAt: string;
  status: string;
}

const AdminPage: React.FC = () => {
  const [stats, setStats] = useState<AdminStats | null>(null);
  const [users, setUsers] = useState<User[]>([]);
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState<'dashboard' | 'users' | 'posts'>('dashboard');
  const [userSearchTerm, setUserSearchTerm] = useState('');
  const [postSearchTerm, setPostSearchTerm] = useState('');

  useEffect(() => {
    loadStats();
  }, []);

  useEffect(() => {
    if (activeTab === 'users') {
      loadUsers();
    } else if (activeTab === 'posts') {
      loadPosts();
    }
  }, [activeTab]);

  const loadStats = async () => {
    try {
      const data = await adminApi.getStats();
      setStats(data);
      setLoading(false);
    } catch (err: any) {
      setError(err.message || 'Áµ±Ë®à„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
      setLoading(false);
    }
  };

  const loadUsers = async () => {
    try {
      const data = await adminApi.getAllUsers();
      setUsers(data);
    } catch (err: any) {
      alert(err.message || '„É¶„Éº„Ç∂„Éº„É™„Çπ„Éà„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  const loadPosts = async () => {
    try {
      const data = await adminApi.getAllPosts();
      setPosts(data);
    } catch (err: any) {
      alert(err.message || 'ÊäïÁ®ø„É™„Çπ„Éà„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  const handleUserStatusChange = async (userId: number, newStatus: string) => {
    if (!window.confirm('„É¶„Éº„Ç∂„Éº„Çπ„ÉÜ„Éº„Çø„Çπ„ÇíÂ§âÊõ¥„Åó„Åæ„Åô„ÅãÔºü')) return;

    try {
      await adminApi.updateUserStatus(userId, newStatus);
      alert('„É¶„Éº„Ç∂„Éº„Çπ„ÉÜ„Éº„Çø„Çπ„ÅåÂ§âÊõ¥„Åï„Çå„Åæ„Åó„Åü„ÄÇ');
      loadUsers();
    } catch (err: any) {
      alert(err.message || '„Çπ„ÉÜ„Éº„Çø„ÇπÂ§âÊõ¥„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  const handleDeletePost = async (postId: number) => {
    if (!window.confirm('ÊäïÁ®ø„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü')) return;

    try {
      await adminApi.deletePost(postId);
      alert('ÊäïÁ®ø„ÅåÂâäÈô§„Åï„Çå„Åæ„Åó„Åü„ÄÇ');
      loadPosts();
    } catch (err: any) {
      alert(err.message || 'ÊäïÁ®ø„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  // „É¶„Éº„Ç∂„Éº„Éï„Ç£„É´„Çø„É™„É≥„Ç∞
  const filteredUsers = users.filter(user => {
    const searchLower = userSearchTerm.toLowerCase();
    return (
      user.username?.toLowerCase().includes(searchLower) ||
      user.nickname?.toLowerCase().includes(searchLower) ||
      user.email?.toLowerCase().includes(searchLower) ||
      user.role?.toLowerCase().includes(searchLower) ||
      user.status?.toLowerCase().includes(searchLower)
    );
  });

  // ÊäïÁ®ø„Éï„Ç£„É´„Çø„É™„É≥„Ç∞
  const filteredPosts = posts.filter(post => {
    const searchLower = postSearchTerm.toLowerCase();
    return (
      post.title?.toLowerCase().includes(searchLower) ||
      post.author?.toLowerCase().includes(searchLower) ||
      post.boardName?.toLowerCase().includes(searchLower)
    );
  });

  if (loading) {
    return (
      <div className="admin-page">
        <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-page">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-header">
        <h1>ÁÆ°ÁêÜËÄÖ„Éö„Éº„Ç∏</h1>
      </div>

      <div className="admin-tabs">
        <button
          className={`tab-button ${activeTab === 'dashboard' ? 'active' : ''}`}
          onClick={() => setActiveTab('dashboard')}
        >
          „ÉÄ„ÉÉ„Ç∑„É•„Éú„Éº„Éâ
        </button>
        <button
          className={`tab-button ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          „É¶„Éº„Ç∂„ÉºÁÆ°ÁêÜ
        </button>
        <button
          className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
          onClick={() => setActiveTab('posts')}
        >
          ÊäïÁ®øÁÆ°ÁêÜ
        </button>
      </div>

      <div className="admin-content">
        {activeTab === 'dashboard' && stats && (
          <div className="dashboard">
            <h2>„Ç∑„Çπ„ÉÜ„É†Áµ±Ë®à</h2>
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon">üë•</div>
                <div className="stat-info">
                  <div className="stat-value">{stats.totalUsers}</div>
                  <div className="stat-label">Á∑è„É¶„Éº„Ç∂„ÉºÊï∞</div>
                </div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">üìù</div>
                <div className="stat-info">
                  <div className="stat-value">{stats.totalPosts}</div>
                  <div className="stat-label">Á∑èÊäïÁ®øÊï∞</div>
                </div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">üí¨</div>
                <div className="stat-info">
                  <div className="stat-value">{stats.totalComments}</div>
                  <div className="stat-label">Á∑è„Ç≥„É°„É≥„ÉàÊï∞</div>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'users' && (
          <div className="users-management">
            <h2>„É¶„Éº„Ç∂„ÉºÁÆ°ÁêÜ</h2>
            <div className="search-container">
              <input
                type="text"
                placeholder="„É¶„Éº„Ç∂„ÉºÂêç„ÄÅ„Éã„ÉÉ„ÇØ„Éç„Éº„É†„ÄÅ„É°„Éº„É´„ÄÅÂΩπÂâ≤„ÄÅ„Çπ„ÉÜ„Éº„Çø„Çπ„ÅßÊ§úÁ¥¢..."
                value={userSearchTerm}
                onChange={(e) => setUserSearchTerm(e.target.value)}
                className="search-input"
              />
              {userSearchTerm && (
                <button
                  onClick={() => setUserSearchTerm('')}
                  className="clear-search"
                >
                  ‚úï
                </button>
              )}
            </div>
            <div className="table-container">
              <table className="admin-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>„É¶„Éº„Ç∂„ÉºÂêç</th>
                    <th>„Éã„ÉÉ„ÇØ„Éç„Éº„É†</th>
                    <th>„É°„Éº„É´</th>
                    <th>ÂΩπÂâ≤</th>
                    <th>„Çπ„ÉÜ„Éº„Çø„Çπ</th>
                    <th>ÁôªÈå≤Êó•</th>
                    <th>„Ç¢„ÇØ„Ç∑„Éß„É≥</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>{user.username}</td>
                      <td>{user.nickname}</td>
                      <td>{user.email}</td>
                      <td>
                        <span className={`badge ${user.role?.toLowerCase()}`}>
                          {user.role || 'USER'}
                        </span>
                      </td>
                      <td>
                        <span className={`badge ${user.status?.toLowerCase()}`}>
                          {user.status || 'ACTIVE'}
                        </span>
                      </td>
                      <td>{new Date(user.joinDate).toLocaleDateString('ko-KR')}</td>
                      <td>
                        <select
                          onChange={(e) => handleUserStatusChange(user.id, e.target.value)}
                          defaultValue=""
                          className="action-select"
                        >
                          <option value="" disabled>„Ç¢„ÇØ„Ç∑„Éß„É≥ÈÅ∏Êäû</option>
                          <option value="ACTIVE">„Ç¢„ÇØ„ÉÜ„Ç£„ÉñÂåñ</option>
                          <option value="INACTIVE">Èùû„Ç¢„ÇØ„ÉÜ„Ç£„ÉñÂåñ</option>
                          <option value="BANNED">„Éñ„É≠„ÉÉ„ÇØ</option>
                        </select>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {filteredUsers.length === 0 && (
                <p className="no-data">
                  {userSearchTerm ? 'Ê§úÁ¥¢ÁµêÊûú„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ' : '„É¶„Éº„Ç∂„Éº„Åå„ÅÑ„Åæ„Åõ„Çì„ÄÇ'}
                </p>
              )}
            </div>
          </div>
        )}

        {activeTab === 'posts' && (
          <div className="posts-management">
            <h2>ÊäïÁ®øÁÆ°ÁêÜ</h2>
            <div className="search-container">
              <input
                type="text"
                placeholder="„Çø„Ç§„Éà„É´„ÄÅ‰ΩúÊàêËÄÖ„ÄÅÊé≤Á§∫Êùø„ÅßÊ§úÁ¥¢..."
                value={postSearchTerm}
                onChange={(e) => setPostSearchTerm(e.target.value)}
                className="search-input"
              />
              {postSearchTerm && (
                <button
                  onClick={() => setPostSearchTerm('')}
                  className="clear-search"
                >
                  ‚úï
                </button>
              )}
            </div>
            <div className="table-container">
              <table className="admin-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>„Çø„Ç§„Éà„É´</th>
                    <th>‰ΩúÊàêËÄÖ</th>
                    <th>Êé≤Á§∫Êùø</th>
                    <th>Èñ≤Ë¶ßÊï∞</th>
                    <th>‰ΩúÊàêÊó•</th>
                    <th>„Ç¢„ÇØ„Ç∑„Éß„É≥</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredPosts.map((post) => (
                    <tr key={post.id}>
                      <td>{post.id}</td>
                      <td className="title-cell">{post.title}</td>
                      <td>{post.author}</td>
                      <td>{post.boardName}</td>
                      <td>{post.viewCount}</td>
                      <td>{new Date(post.createdAt).toLocaleDateString('ko-KR')}</td>
                      <td>
                        <button
                          onClick={() => handleDeletePost(post.id)}
                          className="delete-btn"
                        >
                          ÂâäÈô§
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {filteredPosts.length === 0 && (
                <p className="no-data">
                  {postSearchTerm ? 'Ê§úÁ¥¢ÁµêÊûú„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ' : 'ÊäïÁ®ø„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ'}
                </p>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
