  hotPosts: HotPost[];
  bestBoards: Board[];
}

const RightSidebar: React.FC<RightSidebarProps> = ({ hotPosts, bestBoards }) => {
  return (
    <div className="right-sidebar">
      <div className="sidebar-section">
        <h3 className="section-title">üî• Ïù∏Í∏∞ Í≤åÏãúÍ∏Ä</h3>
        <div className="hot-posts-list">
          {hotPosts.length > 0 ? (
            hotPosts.map((post) => (
              <div key={post.id} className="hot-post-item">
                <div className="post-title">{post.title}</div>
                <div className="post-info">
                  <span className="author">{post.author}</span>
                  <div className="post-stats">
                    <span className="stat">üëÅ {post.viewCount}</span>
                    <span className="stat">üí¨ {post.commentCount}</span>
                    <span className="stat">‚ù§Ô∏è {post.likeCount}</span>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="no-content">Ïù∏Í∏∞ Í≤åÏãúÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.</div>
          )}
        </div>
      </div>

      <div className="sidebar-section">
        <h3 className="section-title">üìå Ïù∏Í∏∞ Í≤åÏãúÌåê</h3>
        <div className="best-boards-list">
          {bestBoards.length > 0 ? (
            bestBoards.map((board) => (
              <div key={board.id} className="board-item">
                <div className="board-header">
                  {board.icon && <span className="board-icon">{board.icon}</span>}
                  <span className="board-name">{board.name}</span>
                </div>
                {board.description && (
                  <div className="board-description">{board.description}</div>
                )}
                <div className="board-stats">
                  Í≤åÏãúÍ∏Ä {board.postCount}Í∞ú
                </div>
              </div>
            ))
